<header class="header">
    <div class="notification-container">
        <input type="checkbox" id="notification-toggle" class="notification-checkbox">
        <label for="notification-toggle" class="notification-button">

		<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" class="notification-bell-icon"><path d="M12 3C9.5 3 7.5 5 7.5 7.5V14L5 16.5H19L16.5 14V7.5C16.5 5 14.5 3 12 3ZM10 16.5C10 17.6 10.9 18.5 12 18.5C13.1 18.5 14 17.6 14 16.5M12 3V1.5" fill="none" stroke="currentColor" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"/></svg>
		<span class="notification-count">
                {% set notification_count = 1 %}
                {{ notification_count }}
            </span>
        </label>

        <div class="notification-content">
            <div class="notification-header">Notifications</div>

            <!-- Notification 1: Abandoned Cart -->
            {% if display == "cart" %}
                <div class="notification-item">
                    <span class="notification-message">
                        <span class="notification-icon">🛒</span>
                        You have items waiting in your cart
                        <a href="{{params.cartpage}}" class="notification-link">View cart</a>
                    </span>
                </div>

            <!-- Notification 2: Abandoned Browse -->
            {% elif display == "reco" %}
            <div class="notification-item">
                <span class="notification-message">
                    <span class="notification-icon">🎁</span>
                    Want to take another peek?
                </span>
                {% for item in reco %}
                    <div class="br-card">
                        <div class="br-card-content">
                            <div class="br-img">
                                <img src="{{ item.image }}" alt="{{ item.title }}">
                                <div class="br-price">{{ params.currency }} {{ item.price }}</div>
                            </div>
                            <div class="br-card-description">
                                <div class="br-title">{{ item.title }}</div>
                            </div>
                        </div>
                    </div>
                {% endfor %}
            </div>

            <!-- Notification 3: Welcome message for new users -->
            {% elif display == "welcome" %}
                <div class="notification-item">
                    <span class="notification-message">
                        <span class="notification-icon">👋</span>
                        Welcome to {{params.brand}}! Sign up to our newsletters and hear about new arrivals first!
                    </span>
                </div>
            {% endif %}
        </div>
    </div>
</header>

