var PARAM_positionVertical = "Bottom";
var PARAM_positionHorizontal = "Right";
var PARAM_enterAnimation = "Fade in";
var PARAM_parentElement = "body";

var NUM_questions = "4";


var self = this;

if (self.inPreview) {
    // PARAM_parentElement = 'body';
}

var showDelay = parseInt('10000' || '0', 1);
var currentStep = 1;
var totalSteps = 4; // Adjust this based on your total number of questions

function showStep(step) {
    document.querySelectorAll('.step-content').forEach(el => el.classList.remove('active'));
    document.querySelector(`.step-content[data-step="${step}"]`).classList.add('active');

    document.querySelectorAll('.step-indicator .step').forEach((el, index) => {
        el.classList.toggle('active', index < step);
    });

    document.getElementById('prev').style.display = step > 1 ? 'inline-block' : 'none';
    document.getElementById('next').style.display = step < totalSteps ? 'inline-block' : 'none';
    document.getElementById('submit').style.display = step === totalSteps ? 'inline-block' : 'none';

    // Update progress bar
    const progress = ((step - 1) / (totalSteps - 1)) * 100;
    document.querySelector('.exponea-progress').style.width = `${progress}%`;
}

function nextStep() {
    if (currentStep < totalSteps) {
        currentStep++;
        showStep(currentStep);
    }
}

function prevStep() {
    if (currentStep > 1) {
        currentStep--;
        showStep(currentStep);
    }
}

setTimeout(function() {
    self.sdk.track('banner', getEventProperties('show', false));
    requestAnimationFrame(function() {
        var parentElement = document.querySelector(PARAM_parentElement);

