function createStaticQuestion(question, answers, trackingKey, containsDynamicAnswer, stepNumber) {
    var questionWrapper = document.createElement("div");
    questionWrapper.classList.add('question-wrapper');

    var exponeaQuestion = document.createElement("span");
    exponeaQuestion.classList.add('exponea-question');
    exponeaQuestion.innerText = question;

    var exponeaButtons = document.createElement("div");
    exponeaButtons.classList.add('buttons');

    var answersSplited = answers.trim().split(";");
    for (var i = 0; i < answersSplited.length; i++) {
        var exponeaAnswer = document.createElement("button");
        exponeaAnswer.innerText = answersSplited[i];
        exponeaAnswer.setAttribute("trackingKey", trackingKey);
        exponeaButtons.appendChild(exponeaAnswer);
    }

    if (containsDynamicAnswer) {
        var customBrand = document.createElement("div");
        customBrand.classList.add('custom-brand');
        var customBrandSpan = document.createElement("span");
        customBrandSpan.innerText = "+";
        var customBrandInput = document.createElement("input");
        customBrandInput.type = "text";
        customBrandInput.placeholder = "Custom";
        customBrandInput.setAttribute("trackingKey", trackingKey);

        customBrand.appendChild(customBrandSpan);
        customBrand.appendChild(customBrandInput);
        exponeaButtons.appendChild(customBrand);
    }

    questionWrapper.appendChild(exponeaQuestion);
    questionWrapper.appendChild(exponeaButtons);

    var stepContent = document.querySelector(`.step-content[data-step="${stepNumber}"]`);
    stepContent.appendChild(questionWrapper);
}

function enableButtonsToggle() {
    var buttons = document.querySelectorAll(".buttons button");
    for (var i = 0; i < buttons.length; i++) {
        buttons[i].addEventListener('click', function() {
            this.classList.toggle('clicked');
            checkSubmitVisibility();
        });
    }
}

function enableCustomBrandToggle() {
    var customBrands = document.querySelectorAll(".buttons div.custom-brand input[type=text]");
    for (var i = 0; i < customBrands.length; i++) {
        customBrands[i].addEventListener('keyup', function() {
            if (this.value.length > 0 && !this.parentNode.classList.contains("clicked")) {
                this.parentNode.classList.add('clicked');
            } else if (this.value.length == 0 && this.parentNode.classList.contains("clicked")) {
                this.parentNode.classList.remove('clicked');
            }
            checkSubmitVisibility();
        });
    }
}

function checkSubmitVisibility() {
    var buttons = document.querySelectorAll(".buttons button.clicked");
    var customBrands = document.querySelectorAll(".buttons div.custom-brand.clicked");
    var submitButton = document.querySelector('button#submit');
    if (buttons.length > 0 || customBrands.length > 0) {
        submitButton.style.display = "inline-block";
    } else {
        submitButton.style.display = "none";
    }
}

function trackAnswers(trackedProperties) {
    var buttons = document.querySelectorAll(".buttons button.clicked");
    var customBrands = document.querySelectorAll(".buttons div.custom-brand.clicked input");
    var result = [];
    if (buttons.length > 0) {
        result = Array.from(buttons);
    }
    if (customBrands.length > 0) {
        result = result.concat(Array.from(customBrands));
    }

    var groupped = groupBy(result, "trackingKey");
    for (const [key, value] of Object.entries(groupped)) {
        var trackedValues = [];
        for (var i = 0; i < value.length; i++) {
            if (value[i].innerText)
                trackedValues.push(value[i].innerText);
            else if (value[i].value)
                trackedValues.push(value[i].value);
        }

        trackedProperties[key] = trackedValues.join(";");
    }

    return trackedProperties;
}

function groupBy(array, key) {
    return array.reduce(function(rv, x) {
        var att = x.getAttribute(key);
        if (rv[att])
            rv[att].push(x);
        else
            rv[att] = [x];
        return rv;
    }, []);
}

function getEventProperties(action, interactive) {
    return {
        action: action,
        banner_id: self.data.banner_id,
        banner_name: self.data.banner_name,
        banner_type: self.data.banner_type,
        variant_id: self.data.variant_id,
        variant_name: self.data.variant_name,
        interaction: interactive !== false ? true : false,
        location: window.location.href,
        path: window.location.pathname
    };
}

function getPositionAndAnimationClasses() {
    var verticalClass = {
        Top: 'vertical-top',
        Center: 'vertical-center',
        Bottom: 'vertical-bottom',
    }[PARAM_positionVertical] || '';

    var horizontalClass = {
        Left: 'horizontal-left',
        Center: 'horizontal-center',
        Right: 'horizontal-right',
    }[PARAM_positionHorizontal] || '';

    var enterAnimationClass = {
        'Fade in': 'enter-fade',
        'Slide in': {
            Top: {
                Left: 'enter-slide-left',
                Center: 'enter-slide-up',
                Right: 'enter-slide-right',
            }[PARAM_positionHorizontal],
            Center: {
                Left: 'enter-slide-left',
                Center: 'enter-slide-up',
                Right: 'enter-slide-right',
            }[PARAM_positionHorizontal],
            Bottom: {
                Left: 'enter-slide-left',
                Center: 'enter-slide-down',
                Right: 'enter-slide-right',
            }[PARAM_positionHorizontal],
        }[PARAM_positionVertical],
    }[PARAM_enterAnimation] || '';

    return verticalClass + ' ' + horizontalClass + ' ' + enterAnimationClass;
}

function removeBanner() {
    if (self.banner) {
        self.banner.parentNode.removeChild(self.banner);
    }
}

return {
    remove: removeBanner
};

